import 'package:flutter/material.dart';List<Widget> _buildPageIndicator(int number) {  List<Widget> list = [];  if(number==0) {    list.add(_indicator(true));    list.add(_indicator(false));  }else {    list.add(_indicator(false));  list.add(_indicator(true));  }  return list;}Widget _indicator(bool isActive) {  return AnimatedContainer(    duration: Duration(milliseconds: 150),    margin: EdgeInsets.symmetric(horizontal: 8.0),    height: 8.0,    width: isActive ? 24.0 : 16.0,    decoration: BoxDecoration(      color: isActive ? Colors.white :  Color(0xFF009688),      borderRadius: BorderRadius.all(Radius.circular(12)),    ),  );}