import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:hygeia/users/view/auth/widget.login.dart';class Page1 extends StatefulWidget {  const Page1({Key key}) : super(key: key);  @override  _Page1State createState() => _Page1State();}class _Page1State extends State<Page1> {  @override  Widget build(BuildContext context) {    double height = MediaQuery.of(context).size.height ;    double width = MediaQuery.of(context).size.width;    return Scaffold(      body: SafeArea(       child: Stack(        children: [          Container(            decoration: new BoxDecoration(image: new DecorationImage(image: new AssetImage("assets/images/bg_page1.png"), fit: BoxFit.cover)),          ),          Column(            children: [              SizedBox(                height: height*0.14,              ),              Padding(                padding: EdgeInsets.symmetric(horizontal: 15),                  child: Text(                "RÉSERVER UN RENDEZ-VOUS",                style: GoogleFonts.archivo(color: Colors.white,fontWeight: FontWeight.bold,fontSize: 22,),                textAlign: TextAlign.center,              )),              SizedBox(                height: height*0.06,              ),              Text("En ligne avec votre medecin,en toute securité efficacité et facilité!",              style: GoogleFonts.archivo(                color: Colors.white,                fontSize: 18,                fontWeight: FontWeight.w500              ),              textAlign: TextAlign.center,),              SizedBox(                height: height*0.17,              ),              Image.asset('assets/images/picrdv.png'),              SizedBox(                height: height*0.03,              ),              Row(                mainAxisAlignment: MainAxisAlignment.center,                children: _buildPageIndicator(0),              ),              SizedBox(                height: height*0.03,              ),              RaisedButton(                padding: EdgeInsets.symmetric(horizontal: 32,vertical: 16),                shape: RoundedRectangleBorder(                    borderRadius: BorderRadius.circular(40)),                child: Text("Commencer",style: GoogleFonts.archivo(                    color: Colors.white,                    fontSize: 18*height * 0.0015,                  fontWeight: FontWeight.w500                ),),                onPressed: ()async  {                  Navigator.push(context,                      MaterialPageRoute(builder: (context)=>Login()));                },color: Color.fromRGBO(8, 96, 160, 1),),            ],          ),        ],      )),    );  }}List<Widget> _buildPageIndicator(int number) {  List<Widget> list = [];  if(number==0) {    list.add(_indicator(true));    list.add(_indicator(false));  }else {    list.add(_indicator(false));    list.add(_indicator(true));  }  return list;}Widget _indicator(bool isActive) {  return AnimatedContainer(    duration: Duration(milliseconds: 150),    margin: EdgeInsets.symmetric(horizontal: 8.0),    height: 8.0,    width: isActive ? 24.0 : 24.0,    decoration: BoxDecoration(      color: isActive ?     Color.fromRGBO(8, 96, 160, 1):Color.fromRGBO(122, 185, 191, 1),      borderRadius: BorderRadius.all(Radius.circular(12)),    ),  );}