import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';class Page2 extends StatefulWidget {  const Page2({Key key}) : super(key: key);  @override  _Page2State createState() => _Page2State();}class _Page2State extends State<Page2> {  @override  Widget build(BuildContext context) {    double height = MediaQuery.of(context).size.height ;    double width= MediaQuery.of(context).size.width;    return Scaffold(      body: Stack(      children: [        Container(            decoration:  BoxDecoration(                image: DecorationImage( image: AssetImage("assets/images/bg3.png"),                    fit: BoxFit.cover),),),        Column(          children: [            SizedBox(height: 70,),            Center(child: Image.asset('assets/images/logo.png',height: 180,width: 280,)),            SizedBox(              height: height *0.25,            ),            Padding(              padding: EdgeInsets.symmetric(horizontal: width * 0.05),                child: Text("«Car votre santé est notre priorité»",style: GoogleFonts.archivo(              color: Colors.white,              fontSize: 26,              fontWeight: FontWeight.bold,            ),              textAlign: TextAlign.center,            )),            SizedBox(              height: height*0.12,            ),            Row(              mainAxisAlignment: MainAxisAlignment.center,              children: _buildPageIndicator(1),            ),            SizedBox(              height: height*0.03,            ),          ],        )      ],),);  }}List<Widget> _buildPageIndicator(int number) {  List<Widget> list = [];  if(number==0) {    list.add(_indicator(true));    list.add(_indicator(false));  }else {    list.add(_indicator(false));    list.add(_indicator(true));  }  return list;}Widget _indicator(bool isActive) {  return AnimatedContainer(    duration: Duration(milliseconds: 150),    margin: EdgeInsets.symmetric(horizontal: 8.0),    height: 8.0,    width: isActive ? 24.0 : 24.0,    decoration: BoxDecoration(      color: isActive ?    Color.fromRGBO(8, 96, 160, 1) :Color.fromRGBO(122, 185, 191, 1),      borderRadius: BorderRadius.all(Radius.circular(12)),    ),  );}