import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:google_fonts/google_fonts.dart';import 'package:hygeia/users/view/auth/widget.login.dart';import 'component/widget.page1.dart';import 'component/widget.page2.dart';class OnboardingScreen extends StatefulWidget {  @override  _OnboardingScreenState createState() => _OnboardingScreenState();}class _OnboardingScreenState extends State<OnboardingScreen> {  final int _numPages = 2;  final PageController _pageController = PageController(initialPage: 0);  int _currentPage = 0;  color1() {    if (_currentPage == 0) {      return 0xFFf2e2b1;    } else if (_currentPage == 1) {      return 0xFFd5d5d5;    } else if (_currentPage == 2) {      return 0xFFf15e68;    }  }  @override  Widget build(BuildContext context) {    double height = MediaQuery.of(context).size.height;    double width = MediaQuery.of(context).size.width;    return Scaffold(      body: AnnotatedRegion<SystemUiOverlayStyle>(        value: SystemUiOverlayStyle.light,        child: Container(          color: Colors.blue,          child: Padding(            padding: EdgeInsets.symmetric(vertical: 0),            child: ListView(              children: <Widget>[                Container(                  height: height,                  child: PageView(                    physics: ClampingScrollPhysics(),                    controller: _pageController,                    onPageChanged: (int page) {                      setState(() {                        _currentPage = page;                      });                    },                    children: <Widget>[                      Page2(),                     Page1(),                    ],                  ),                ),              ],            ),          ),        ),      ),    );  }}