import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';InputDecoration decoration(String hintText, double height ) {  return InputDecoration(    errorStyle:   GoogleFonts.archivo(      fontSize: 10*height * 0.0015,      color: Colors.red,    ),    hintStyle: GoogleFonts.archivo(color: Color.fromRGBO(148, 148, 158, 0.6,),fontSize: 13,fontStyle: FontStyle.italic),    hintText: hintText,    contentPadding: new EdgeInsets.symmetric(vertical: 15.0, horizontal: 15.0),    fillColor: Colors.white,    filled: true,    errorBorder: OutlineInputBorder(      borderRadius: BorderRadius.circular(30),      borderSide:  BorderSide(        color: Colors.white,        width: 0.0,      ),    ),    focusedErrorBorder:    OutlineInputBorder(      borderRadius: BorderRadius.circular(30),      borderSide:  BorderSide(        color: Colors.white,        width: 0.0,),),    enabledBorder: OutlineInputBorder(      borderRadius: BorderRadius.circular(30),      borderSide:  BorderSide(        color: Colors.white,        width: 0.0,      ),    ),    focusedBorder: OutlineInputBorder(      borderRadius: BorderRadius.circular(30),      borderSide:  BorderSide(        color: Colors.white,        width: 0.0,      ),    ),  );}